import javax.swing.JOptionPane;

public class KnockKnock {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		JOptionPane.showMessageDialog(null, "Knock Knock");
		JOptionPane.showMessageDialog(null, "Who's There?");
		JOptionPane.showMessageDialog(null, "Boo!");
		JOptionPane.showMessageDialog(null, "Boo who?");
		JOptionPane.showMessageDialog(null, "Why are you so sad?");
	}

}
